(function(tinymce){tinymce.create('tinymce.plugins.NewProjPlugin',{init:function(ed,url){ed.addCommand('mceNewproj',function(){ed.windowManager.open({file:url+'/emotions.htm',width:750+parseInt(ed.getLang('emotions.delta_width',0)),height:360+parseInt(ed.getLang('emotions.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('newproj',{title:'emotions.emotions_desc',cmd:'mceNewproj'})},getInfo:function(){return{longname:'NewProject',author:'Moxiecode Systems AB',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/emotions',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('newproj',tinymce.plugins.EmotionsPlugin)})(tinymce);